mapper.insert:
insert into category (type, name, sort, status, create_time, update_time, create_user, update_user)
values (#{type}, #{name}, #{sort}, #{status},
        #{createTime},#{updateTime},#{createUser}, #{updateUser});

mapper.update:
update category
type = #{type}, name = #{name}, sort = #{sort}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
where id = #{id}
