mapper.update:
update dish
category_id = #{categoryId}, name = #{name}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select d.*, c.name as categoryName from dish d left outer join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')



        and d.category_id = #{categoryId}



        and d.status = #{status}
